var mongoose = require('mongoose');

var Schema = mongoose.Schema;

var userSchema = mongoose.Schema({ 
	token : String,
	email: String, 
	hashed_password: String, 
	salt : String,
	temp_str:String
});

var membresSchema = mongoose.Schema({ 
	token : String,
	nom : String,
	prenom: String,
	contact: String,
	adresse: String,
	anniversaire: String
});

var cellulesSchema = mongoose.Schema({ 
	token : String,
	nom : String,
	jour : String,
	debut : String,
	fin : String,
	lieu : String,
	chef : String
});
var menageSchema = mongoose.Schema({ 
	token : String,
	date : Date,
	membre : String
});
var pbSchema = mongoose.Schema({ 
	token : String,
	mois : String,
	personnage1 : String,
	groupe1 : String,
	personnage2 : String,
	groupe2 : String,
	personnage3 : String,
	groupe3 : String
});
var texteSchema = mongoose.Schema({ 
	token : String,
	mois : String,
	libelle : String
});
var groupeebSchema = mongoose.Schema({ 
	token : String,
	texte : String,
	date : Date,
	membre : String
});
var mvfSchema = mongoose.Schema({ 
	token : String,
	mois : String,
	theme : String
});
var veilleeSchema = mongoose.Schema({ 
	token : String,
	mois : String,
	theme : String,
	membre : String
});
var biblioSchema = mongoose.Schema({ 
	token : String,
	mois : String,
	libelle : String,
	membre : String
});
var rgSchema = mongoose.Schema({ 
	token : String,
	date : Date,
	membre : String
});
var programmesSchema = mongoose.Schema({ 
	token : String,
	libelle : String,
	date : Date
})

exports.membres = mongoose.model('Membre',membresSchema);
exports.cellules = mongoose.model('cellules',cellulesSchema);
exports.menage = mongoose.model('menage',menageSchema);
exports.groupeeb = mongoose.model('groupeeb',groupeebSchema);
exports.pb = mongoose.model('pb',pbSchema);
exports.texte = mongoose.model('texte',texteSchema);
exports.mvf = mongoose.model('mvf',mvfSchema);
exports.veillee = mongoose.model('veillee',veilleeSchema);
exports.biblio = mongoose.model('biblio', biblioSchema);        
exports.rg = mongoose.model('rg', rgSchema);  
exports.programmes = mongoose.model('programmes', programmesSchema);

